def main():

	# clear output from webdriver
	print("")

	# input data for the forms
	#firstName = input("Enter your first name:")
	#lastName = input("Enter your last name:")
	#email = input("Enter your e-mail address:")
	#phone = input("Enter your phone number:")

	# data for the from
	firstName = "test"
	lastName = "test"
	email = "test@test.com"
	phone = "1234567"

	#serviceUrl = input("Enter link to the desired service:")
	serviceUrl = "https://service.berlin.de/dienstleistung/121721/"

	# district which to considere for appointments only
	#districtOffice = input("Enter the desired district office (optional):")
	#districtOffice = "Bezirksamt Mitte"
	districtOffice = ""

	delayInMinutes = 2
	delayInSeconds = 0
	delayBetweenAttempts = delayInMinutes*60 + delayInSeconds

	# get desired service url and extract title
	driver.get(serviceUrl)
	titles = driver.find_elements(By.XPATH, "//h1[contains(@class, 'title')]")

	# print query parameters
	print("Searching for an Appointment for: " + titles[0].text)
	print("At: " + districtOffice)
	print("")

	while True:

		# make an appointment
		if (findAppointment(serviceUrl, districtOffice, firstName, lastName, email, phone)):
			break

		print("No appointment was made. Restarting in " + str(delayBetweenAttempts) + "seconds.")
		time.sleep(delayBetweenAttempts)
		print("")

if __name__ == "__main__":
    main()